import React, { Component } from 'react'
import axios from 'axios'

class AllBeer extends Component {
    constructor() {
        super()
        this.state = {
            Beers: []
        }
    }

    componentDidMount() {
        axios.get("http://206.189.7.127/countries/")
            .then(response => {
                this.setState({ countries: response.data })
            })
    }

    render() {
        return (
            <div>
                Hola
            </div>
        )
    }



}


export default AllBeer
