import React, { Component } from 'react'
import axios from 'axios'
import Beers from '../components/Beer-Card'
import Nav from '../components/Header'
class AllBeer extends Component {
    constructor() {
        super()
        this.state = {
            beers: []
        }
    }

    componentDidMount() {
        axios.get("https://ih-beer-api.herokuapp.com/beers")
            .then(res => {
                this.setState({ beers: res.data })
            })
    }

    render() {
        console.log(this.state.beers.slice(0, 50))
        { this.state.beers = this.state.beers.slice(0, 50) }
        return (
            <div>
                <Nav />
                {this.state.beers.map((beer, idx) => <Beers key={idx} {...beer} />)}
            </div>
        )
    }



}


export default AllBeer
