import React, { Component } from 'react'
import { Link } from 'react-router-dom'
import BeersApi from '../services/beers-api'



class BeerDetails extends Component {

    constructor(props) {
        super(props)
        this.state = { beers: {} }
        this.services = new BeersApi()

    }

    componentDidMount() {
        this.services.getOneBeer(this.props.match.params.id)
            .then(beer => this.setState({ beers: beer }))
    }



    render() {
        const { image, name, tagline, first_brewed, attenuation_level, description, contributed_by } = this.state.beers
        return (
            <div >

                <img src={image} alt={name} />

